pub fn solve(input: &str) -> (isize, usize) {
    input
        .chars()
        .enumerate()
        .map(|(index, instr)| (index, if instr == '(' { 1 } else { -1 }))
        .fold((0, 0), |(floor, position), (index, step)| {
            (floor + step, if floor + step < 0 && position == 0 { index } else { position })
        })

}
