pub fn solve(input: &str) -> (isize, usize) {
    let mut floor = 0;
    let mut pos   = 0;
    for (i, c) in input.chars().enumerate() {
        match c {
            '(' => floor += 1,
            ')' => floor -= 1,
            _   => panic!("Bad input")
        }
        if floor < 0 && pos == 0 {
            pos = i + 1;
        }
    }
    (floor, pos)
}
